      sidebarPanel(
        class = "sidebar-panel",
        h4("Inputs"),
        numericInput(ns("portfolio_size"), "Portfolio Size $:", value = 100000, min = 0),
        numericInput(ns("position_size_pct"), "Position Size %:", value = 10, min = 0, max = 100),
        numericInput(ns("desired_return_pct"), "Desired % Return:", value = 40, min = 0, max = 100),
