                div(class = "output-value", textOutput(ns("optimal_f")))
            )
        )
      )
    )
  )
}

RBAF_Server <- function(id) {
  moduleServer(id, function(input, output, session) {
    calculations <- eventReactive(input$calculate, {
      portfolio_size <- input$portfolio_size
      position_size_pct <- input$position_size_pct / 100
      desired_return_pct <- input$desired_return_pct / 100
      avg_gain_pct <- input$avg_gain_pct / 100
      winning_trades_pct <- input$winning_trades_pct / 100
      avg_loss_pct <- input$avg_loss_pct / 100

      if (avg_loss_pct == 0) avg_loss_pct <- 1e-9
      if (winning_trades_pct == 0 && avg_loss_pct == 0) {
        avg_loss_pct <- 1e-9
        avg_gain_pct <- 1e-9
      }

      avg_dollar_gain_on_winning_trades <- avg_gain_pct * (portfolio_size * position_size_pct)
      avg_dollar_loss_on_losing_trades <- avg_loss_pct * (portfolio_size * position_size_pct)
      gain_loss_ratio_non_adj <- avg_gain_pct / avg_loss_pct
      dollar_position_size <- portfolio_size * position_size_pct
      expected_net_return_pct_per_trade <- (winning_trades_pct * avg_gain_pct) - ((1 - winning_trades_pct) * avg_loss_pct)
      expected_dollar_return_per_trade <- expected_net_return_pct_per_trade * dollar_position_size
      dollar_goal <- desired_return_pct * portfolio_size

      trades_needed <- if (expected_dollar_return_per_trade != 0) {
        dollar_goal / expected_dollar_return_per_trade
      } else {
        Inf
      }

      num_winning_trades <- trades_needed * winning_trades_pct
      num_losing_trades <- trades_needed * (1 - winning_trades_pct)

      denominator_adj_ratio <- (1 - winning_trades_pct) * avg_loss_pct
      gain_loss_ratio_adj <- if (denominator_adj_ratio != 0) {
        (winning_trades_pct * avg_gain_pct) / denominator_adj_ratio
      } else {
        Inf
      }

      optimal_f <- if (gain_loss_ratio_non_adj != 0) {
        winning_trades_pct - ((1 - winning_trades_pct) / gain_loss_ratio_non_adj)
      } else {
        0
      }
      optimal_f <- max(0, optimal_f)

      list(
        avg_dollar_gain = avg_dollar_gain_on_winning_trades,
        num_winning_trades = num_winning_trades,
        avg_dollar_loss = avg_dollar_loss_on_losing_trades,
        num_losing_trades = num_losing_trades,
        gain_loss_ratio_non_adj = gain_loss_ratio_non_adj,
        position_size_dollar = dollar_position_size,
        expected_net_return_pct = expected_net_return_pct_per_trade * 100,
        expected_dollar_return = expected_dollar_return_per_trade,
        dollar_goal = dollar_goal,
        trades_needed = trades_needed,
        gain_loss_ratio_adj = gain_loss_ratio_adj,
        optimal_f = optimal_f * 100
      )
    })

    output$avg_dollar_gain <- renderText({
      paste0("$", format(round(calculations()$avg_dollar_gain, 2), nsmall = 2, big.mark = ","))
    })

    output$num_winning_trades <- renderText({
      format(round(calculations()$num_winning_trades), big.mark = ",")
    })

    output$avg_dollar_loss <- renderText({
      paste0("$", format(round(calculations()$avg_dollar_loss, 2), nsmall = 2, big.mark = ","))
    })

    output$num_losing_trades <- renderText({
      format(round(calculations()$num_losing_trades), big.mark = ",")
    })

    output$gain_loss_ratio_non_adj <- renderText({
      format(round(calculations()$gain_loss_ratio_non_adj, 2), nsmall = 2)
    })

    output$position_size_dollar <- renderText({
      paste0("$", format(round(calculations()$position_size_dollar, 2), nsmall = 2, big.mark = ","))
    })

    output$expected_net_return_pct <- renderText({
      paste0(format(round(calculations()$expected_net_return_pct, 2), nsmall = 2), "%")
    })

    output$expected_dollar_return <- renderText({
      paste0("$", format(round(calculations()$expected_dollar_return, 2), nsmall = 2, big.mark = ","))
    })

    output$dollar_goal <- renderText({
      paste0("$", format(round(calculations()$dollar_goal, 2), nsmall = 2, big.mark = ","))
    })

    output$trades_needed <- renderText({
      if (is.infinite(calculations()$trades_needed)) {
        "N/A (Cannot reach goal)"
      } else {
        format(round(calculations()$trades_needed), big.mark = ",")
      }
    })

    output$gain_loss_ratio_adj <- renderText({
      format(round(calculations()$gain_loss_ratio_adj, 2), nsmall = 2)
    })

    output$optimal_f <- renderText({
      paste0(format(round(calculations()$optimal_f, 2), nsmall = 2), "%")
    })

    observeEvent(input$clear_all, {
      updateNumericInput(session, "portfolio_size", value = 0)
      updateNumericInput(session, "position_size_pct", value = 0)
      updateNumericInput(session, "desired_return_pct", value = 0)
      updateNumericInput(session, "avg_gain_pct", value = 0)
      updateNumericInput(session, "winning_trades_pct", value = 0)
      updateNumericInput(session, "avg_loss_pct", value = 0)
    })
  })
}

