drmaUI <- function(id) {
  ns <- NS(id)
  fluidPage(
    titlePanel("DRMA Analysis of Trading Data"),

    sidebarLayout(
      sidebarPanel(
        fileInput(ns("file1"), "Upload CSV File",
                  multiple = FALSE,
                  accept = c("text/csv",
                             "text/comma-separated-values,text/plain",
                             ".csv")),
        tags$hr(),
        helpText("Upload your trading data in CSV format. The CSV should contain columns like 'profit_loss', 'buy_amount', and 'status'.")
      ),

      mainPanel(
        h3("DRMA Table"),
        DTOutput(ns("drmaTable")),
        br(),
        h4("Total # of Complete Trades:"),
        textOutput(ns("totalTrades"))
      )
    )
  )
}

drmaServer <- function(id) {
  moduleServer(id, function(input, output, session) {

    filtered_data <- reactive({
      req(input$file1)
      df <- read_csv(input$file1$datapath, show_col_types = FALSE)

      df_complete <- df %>%
        filter(status == "COMPLETE")

      df_complete <- df_complete %>%
        mutate(profit_loss_percentage = (profit_loss / buy_amount) * 100)

      df_complete
    })

    drma_results <- reactive({
      df <- filtered_data()
      total_complete_trades <- nrow(df)

      if (total_complete_trades == 0) {
        return(data.frame(
          Range = character(),
          `# Gains` = numeric(),
          `# Losses` = numeric(),
          `% Up` = character(),
          `% Down` = character(),
          `Net %` = character(),
          DRMA = numeric(),
          check.names = FALSE
        ))
      }

      ranges_data <- list()

      for (i in seq(0, 20, by = 2)) {
        lower_bound <- i
        upper_bound <- i + 2
        range_label <- paste0(lower_bound, "-", upper_bound, "%")

        num_gains <- df %>%
          filter(profit_loss_percentage >= lower_bound & profit_loss_percentage < upper_bound) %>%
          nrow()

        if (lower_bound == 0) {
          num_losses <- df %>%
            filter(profit_loss_percentage <= 0 & profit_loss_percentage > -upper_bound) %>%
            nrow()
        } else {
          num_losses <- df %>%
            filter(profit_loss_percentage <= -lower_bound & profit_loss_percentage > -upper_bound) %>%
            nrow()
        }

        percent_up <- (num_gains / total_complete_trades) * 100
        percent_down <- (num_losses / total_complete_trades) * 100

        net_percent <- percent_up - percent_down
        total_observations_in_range <- num_gains + num_losses

        drma <- (net_percent / 100) * total_observations_in_range

        ranges_data[[range_label]] <- data.frame(
          Range = range_label,
          `# Gains` = num_gains,
          `# Losses` = num_losses,
          `% Up` = sprintf("%.2f%%", percent_up),
          `% Down` = sprintf("%.2f%%", percent_down),
          `Net %` = sprintf("%.2f%%", net_percent),
          DRMA = round(drma, 2),
          check.names = FALSE
        )
      }

      do.call(rbind, ranges_data)
    })

    output$drmaTable <- renderDT({
      drma_results()
    }, options = list(pageLength = 10,
                      dom = 'tip'),
    rownames = FALSE)

    output$totalTrades <- renderText({
      nrow(filtered_data())
    })
  })
}

