positionSizingServer <- function(id) {
  moduleServer(id, function(input, output, session) {
    output_values <- reactiveValues(
      stop_amount = "",
      num_shares = "",
      amt_position = "",
      position_size_pct = "",
      risk_equity_pct = "",
      r_multiples_data = data.frame(
        Metric = c("Risk", "Upside target based on R", "% Gain"),
        `1R` = rep("", 3), `2R` = rep("", 3), `3R` = rep("", 3),
        `4R` = rep("", 3), `5R` = rep("", 3), `6R` = rep("", 3),
        check.names = FALSE
      )
    )

    observeEvent(input$calculate_ps, {
      portfolio_size <- input$portfolio_size
      share_price <- input$share_price
      stop_price <- input$stop_price
      amount_to_risk <- input$amount_to_risk / 100

      if (share_price <= stop_price) {
        showNotification("Share Price must be greater than Stop Price.", type = "error")
        return()
      }
      if (portfolio_size <= 0) {
        showNotification("Portfolio Size must be greater than 0.", type = "error")
        return()
      }

      risk_per_share <- share_price - stop_price
      max_dollar_risk <- portfolio_size * amount_to_risk
      num_shares_to_buy <- floor(max_dollar_risk / risk_per_share)

      if (num_shares_to_buy <= 0) {
        showNotification("Number of shares to buy is zero. Adjust inputs.", type = "warning")
        output_values$stop_amount = "$0.00"
        output_values$num_shares = "0"
        output_values$amt_position = "$0.00"
        output_values$position_size_pct = "0.00%"
        output_values$risk_equity_pct = "0.00%"
        output_values$r_multiples_data = data.frame(
          Metric = c("Risk", "Upside target based on R", "% Gain"),
          `1R` = rep("$0.00", 3), `2R` = rep("$0.00", 3), `3R` = rep("$0.00", 3),
          `4R` = rep("$0.00", 3), `5R` = rep("$0.00", 3), `6R` = rep("$0.00", 3),
          check.names = FALSE
        )
        return()
      }

      amount_of_position <- num_shares_to_buy * share_price
      position_size_percent <- (amount_of_position / portfolio_size) * 100
      risk_on_equity_percent <- (num_shares_to_buy * risk_per_share / portfolio_size) * 100

      output_values$stop_amount <- paste0("$", format(round(risk_per_share, 2), nsmall = 2, big.mark = ","))
      output_values$num_shares <- format(num_shares_to_buy, big.mark = ",")
      output_values$amt_position <- paste0("$", format(round(amount_of_position, 2), nsmall = 2, big.mark = ","))
      output_values$position_size_pct <- paste0(format(round(position_size_percent, 2), nsmall = 2), "%")
      output_values$risk_equity_pct <- paste0(format(round(risk_on_equity_percent, 2), nsmall = 2), "%")

      r_multiples_df <- data.frame(
        Metric = c("Risk", "Upside target based on R", "% Gain")
      )

      for (r in 1:6) {
        r_multiples_df[[paste0(r, "R")]] <- c(
          paste0("-$", format(round(r * risk_per_share, 2), nsmall = 2, big.mark = ",")),
          paste0("$", format(round(share_price + (r * risk_per_share), 2), nsmall = 2, big.mark = ",")),
          paste0(format(round((r * risk_per_share / share_price) * 100, 2), nsmall = 2), "%")
        )
      }
      output_values$r_multiples_data <- r_multiples_df
    })

    observeEvent(input$clear_ps, {
      updateNumericInput(session, "portfolio_size", value = 0)
      updateNumericInput(session, "share_price", value = 0)
      updateNumericInput(session, "stop_price", value = 0)
      updateNumericInput(session, "amount_to_risk", value = 0)

      output_values$stop_amount = ""
      output_values$num_shares = ""
      output_values$amt_position = ""
      output_values$position_size_pct = ""
      output_values$risk_equity_pct = ""
      output_values$r_multiples_data = data.frame(
        Metric = c("Risk", "Upside target based on R", "% Gain"),
        `1R` = rep("", 3), `2R` = rep("", 3), `3R` = rep("", 3),
        `4R` = rep("", 3), `5R` = rep("", 3), `6R` = rep("", 3),
        check.names = FALSE
      )
    })

    output$stop_amount <- renderText({ output_values$stop_amount })
    output$num_shares <- renderText({ output_values$num_shares })
    output$amt_position <- renderText({ output_values$amt_position })
    output$position_size_pct <- renderText({ output_values$position_size_pct })
    output$risk_equity_pct <- renderText({ output_values$risk_equity_pct })
    output$r_multiples_table <- renderTable({ output_values$r_multiples_data },
                                            bordered = TRUE,
                                            align = 'c',
                                            class = "r-multiples-table")
  })
}

